SET NOTICE_SUBJECT=#{noticeSubject}, NOTICE_CONTENT=#{noticeContent}
WHERE NOTICE_CODE=#{noticeCode}

UPDATE  TBL_FAQ
SET     FAQ_SUBJECT=#{faqSubject}
      , FAQ_CONTENT=#{faqContent}
      , ADMIN_POST_CATEGORY_CODE=#{categoryCode}
WHERE   FAQ_CODE=#{faqCode}

UPDATE TBL_ADMIN
SET ADMIN_EMAIL=#{adminEmail}
  , ADMIN_PWD=#{adminPassword}
  , ADMIN_NICKNAME=#{adminNickname}
WHERE ADMIN_CODE=#{adminCode}

