SET INQ_ANS_CONTENT=#{inquiryAnswerContent}
WHERE INQ_ANS_CODE = 
(
    SELECT INQ_ANS_CODE
     FROM VIEW_INQUIRY_TOTAL
     WHERE INQ_CODE = #{inquiryCode}
)

