SELECT RPT_TYPES AS reportType , RPT_CODE AS reportCode , ASGN_ADMIN_CODE AS assignedAdminCode , ASGN_ADMIN_NICKNAME AS assignedAdminNickname , TO_CHAR(RPT_ACPT_DATE, 'YYYY-MM-DD HH24:MI') AS reportAcceptedDate , APPLICANT_ACCT_CODE AS reporterAccountCode , APPLICANT_ACC_USER_NICKNAME AS reporterNickname , RPT_ASGN_DATE AS reportAssignedDate , RPT_STATUS AS reportStatus , TO_CHAR(RPT_RSLV_DATE, 'YYYY-MM-DD HH24:MI') AS reportResolveDate FROM ( SELECT ROWNUM AS RNUM , V.* FROM VIEW_RPT_TOTAL V WHERE V.APPLICANT_ADMIN_CODE IS NULL
AND V.ASGN_ADMIN_CODE = #{searchValue}
AND V.RPT_STATUS = #{searchValue}
AND V.APPLICANT_ACCT_CODE = #{searchValue}
ORDER BY RPT_ACPT_DATE DESC ) WHERE RNUM >= #{start} AND RNUM <= #{end}


SELECT COUNT(*)
FROM VIEW_RPT_TOTAL V
WHERE V.APPLICANT_ADMIN_CODE IS NULL



	  AND V.ASGN_ADMIN_CODE = #{searchValue}


	  AND V.RPT_STATUS = #{searchValue}


	  AND V.APPLICANT_ACCT_CODE = #{searchValue}

SELECT INQ_CODE AS inquiryCode , INQ_SUBJECT AS inquirySubject , INQ_CONTENT AS inquiryContent , TO_CHAR(INQ_ACPT_DATE, 'YYYY-MM-DD HH24:MI') AS inquiryAcceptedDate , ADMIN_POST_CATEGORY_TYPE AS postCategoryName , USER_ACCT_CODE AS inquirerAccountCode , USER_NICKNAME AS inquirerNickname , ADMIN_CODE AS assignedAdminCode , ADMIN_NICKNAME AS assignedAdminNickname , INQ_ANS_CODE AS inquiryAnswerCode , INQ_ANS_CONTENT AS inquiryAnswerContent , INQ_STATUS AS inquiryStatus FROM ( SELECT ROWNUM AS RNUM , V.* FROM VIEW_INQUIRY_TOTAL V
WHERE V.ADMIN_CODE = #{searchValue}
WHERE V.INQ_STATUS = #{searchValue}
WHERE V.USER_ACCT_CODE = #{searchValue}
ORDER BY INQ_ACPT_DATE DESC ) WHERE RNUM >= #{start} AND RNUM <= #{end}


SELECT COUNT(*)
FROM VIEW_INQUIRY_TOTAL V



	WHERE V.ADMIN_CODE = #{searchValue}


	WHERE V.INQ_STATUS = #{searchValue}


	WHERE V.USER_ACCT_CODE = #{searchValue}

SELECT ADMIN_CODE AS adminCode , ADMIN_EMAIL AS adminEmail , ADMIN_NICKNAME AS adminNickname , CHIEF_OR_NOT AS chiefAdmin FROM ( SELECT ROWNUM AS RNUM , A.ADMIN_CODE , A.ADMIN_EMAIL , A.ADMIN_NICKNAME , YN.YES_NO AS CHIEF_OR_NOT FROM TBL_ADMIN A LEFT JOIN TBL_YES_NO YN ON A.CHIEF_ADMIN_CODE = YN.YES_NO_CODE
WHERE ADMIN_NICKNAME LIKE '%' || #{searchValue} || '%'
WHERE ADMIN_EMAIL LIKE '%' || #{searchValue} || '%'
) WHERE RNUM >= #{start} AND RNUM <= #{end}

{ call PRC_ADMIN_LOGIN
	(
		#{adminEmail},
	    #{adminPassword},
	    #{ipAddress},
	    #{returnValue ,mode=OUT, jdbcType=VARCHAR}
	)
}
