mapper.insert:
INSERT INTO TBL_COURT_REGISTRATION(
    COURT_REG_CODE
    , COURT_REG_DATE
    , COURT_MAP_POSITION
    , COURT_NAME
    , COURT_IMG1
    , COURT_IMG2
    , COURT_IMG3
    , REGISTRANT_ACCT_CODE
    , COURT_REG_CAPACITY
)
VALUES(
    FN_CREATE_CODE('TBL_COURT_REGISTRATION', SEQ_COURT_REGISTRATION.NEXTVAL)
    , SYSDATE
    , #{mapPosition}
    , #{courtName}
    , #{courtImg1}
    , #{courtImg2}
    , #{courtImg3}
    , #{registrantAccountCode}
    , #{courtCapacityCode}
)

mapper.select:
{ call PRC_COURT_DELETE_REQ
	( #{courtCode}
	, #{applicantAccountCode, mode=IN, jdbcType=VARCHAR}
	, #{returnValue, mode=OUT, jdbcType=VARCHAR}
	) 
}

mapper.insert:
INSERT INTO TBL_COURT_NAME(
	COURT_NAME_CODE
	, COURT_NAME
	, COURT_NAME_REG_DATE
	, COURT_REG_CODE
	, REG_ACCT_CODE
)
