	, SYSDATE
)

INSERT INTO TBL_COURT_TELEPHONE(
	COURT_TEL_CODE
	, COURT_TEL
	, COURT_TEL_REG_DATE
	, COURT_REG_CODE
	, REG_ACCT_CODE
)
VALUES(
	FN_CREATE_CODE('TBL_COURT_TELEPHONE',SEQ_COURT_TELEPHONE.NEXTVAL)
	, #{telephone}
	, SYSDATE
	, #{courtCode}
	, #{userAccountCode}
)

mapper.select:
{ call PRC_COURT_REVIEW
	( #{courtReviewContent}
	, #{courtReviewManageScore}
	, #{courtReviewSatisfaction}
	, #{courtCapacityCode}
	, #{courtCode}         
	, #{registrantAccountCode}
	, #{toilet, mode=IN, jdbcType=VARCHAR}
	, #{shower, mode=IN, jdbcType=VARCHAR}
	, #{parkinglot, mode=IN, jdbcType=VARCHAR}
	, #{returnValue, mode=OUT, jdbcType=VARCHAR}
	)
}

{ call PRC_COURT_DEL_REQ_APPROVE
	( #{courtDeleteRequestCode}
	, #{courtCode}
	, #{approvedAdminCode, mode=IN, jdbcType=VARCHAR}
	, #{requestResultCode}
	, #{returnValue, mode=OUT, jdbcType=VARCHAR}
	) 
}

SELECT COURT_NAME_CODE AS courtNameCode , COURT_NAME AS courtName , TO_CHAR(COURT_NAME_REG_DATE,'YYYY-MM-DD HH24:MI') AS courtNameRegisteredDate , COURT_REG_CODE AS courtCode , ADMIN_REG AS adminRegister , REG_ACCT_CODE AS registrantAccountCode , REG_NICKNAME AS registrantNickname , POLL_COUNT AS pollCount , POLL_RATE AS pollRate , COURT_NAME_STATUS AS courtNameStatus , RANK AS rank , CASE WHEN P.VOTER_ACCT_CODE IS NOT NULL THEN 'YES' ELSE 'NO' END AS pollOrNot FROM ( SELECT ROWNUM AS RNUM, V.* FROM VIEW_COURT_NAME V LEFT JOIN ( SELECT * FROM TBL_COURT_NAME_POLL
WHERE VOTER_ACCT_CODE=#{userAccountCode}
) P ON B.COURT_NAME_CODE = P.COURT_NAME_CODE WHERE COURT_NAME_STATUS NOT LIKE '제재' AND COURT_REG_CODE = #{courtCode}
ORDER BY COURT_NAME_REG_DATE DESC, RANK ASC
ORDER BY RANK ASC, COURT_NAME_REG_DATE DESC
) WHERE RNUM >= #{start} AND RNUM <= #{end}

SELECT COURT_REVIEW_CODE AS courtReviewCode , COURT_REVIEW_CONTENT AS courtReviewContent , COURT_REVIEW_MNG_SCORE AS courtReviewManageScore , TO_CHAR(COURT_REVIEW_REG_DATE, 'YYYY-MM-DD HH24:MI') AS courtReviewRegisteredDate , COURT_REVIEW_SATISFACTION AS courtReviewSatisfaction , MIN_COURT_CAPACITY AS minCourtCapacity , MAX_COURT_CAPACITY AS maxCourtCapacity , COURT_REG_CODE AS courtCode , REGISTRANT_ACCT_CODE AS registrantAccountCode , REGISTRANT_NICKNAME AS registrantNickname , COURT_REVIEW_DEL_DATE AS courtReviewDelDate , COURT_REVIEW_STATUS AS courtReviewStatus , LIKES AS likes , DISLIKES AS dislikes , TOILET AS toilet , SHOWER AS shower , PARK AS parkinglot , CASE WHEN VOTER_ACCT_CODE IS NOT NULL THEN 'YES' ELSE 'NO' END AS pollOrNot , CASE WHEN COURT_REVIEW_LIKE_CODE IS NULL THEN NULL WHEN COURT_REVIEW_LIKE_CODE = 'ZU01' THEN 'LIKE' ELSE 'DISLIKE' END AS pollLikeOrDislike FROM ( SELECT T.*, ROWNUM AS RNUM FROM ( SELECT V.*, E.VOTER_ACCT_CODE, E.COURT_REVIEW_LIKE_CODE FROM VIEW_COURT_REVIEW V LEFT JOIN ( SELECT * FROM TBL_COURT_REVIEW_EVAL WHERE VOTER_ACCT_CODE=#{userAccountCode, jdbcType=VARCHAR} ) E ON V.COURT_REVIEW_CODE = E.COURT_REVIEW_CODE WHERE V.COURT_REG_CODE = #{courtCode} AND V.COURT_REVIEW_STATUS NOT LIKE '삭제' AND V.COURT_REVIEW_STATUS NOT LIKE '제재'
ORDER BY (V.LIKES-V.DISLIKES) DESC, V.COURT_REVIEW_REG_DATE DESC
ORDER BY V.COURT_REVIEW_REG_DATE DESC, (V.LIKES-V.DISLIKES) DESC
) T ) WHERE RNUM >= #{start} AND RNUM <= #{end}
