mapper.update:
UPDATE TBL_CREW
SET CREW_TITLE_ACHV_CODE=#{titleAchievementGetCode}
  , CREW_COURT_CODE=#{crewCourtCode} 
  , CREW_FAV_BTL_MODE_CODE=#{crewFavoriteBattleModeCode} 
  , CREW_BTL_REFUSE_CODE=#{crewBattleRefuseCode}
  , CREW_JOIN_MIN_BIRTHYEAR=#{crewJoinMinBirthyear}
  , CREW_JOIN_MAX_BIRTHYEAR=#{crewJoinMaxBirthyear}
  , CREW_JOIN_MIN_FPS=#{crewJoinMinFairplayScore}
  , CREW_JOIN_ABL_LIMIT_CODE=#{crewJoinAbilityLimitCode}
WHERE CREW_CODE = #{crewCode};

UPDATE TBL_CREW_INVITATION
SET CREW_INV_RES_CODE='ZU02', CREW_INV_RSLV_DATE=SYSDATE
WHERE CREW_INV_CODE = #{crewInvitationCode}

mapper.insert:
INSERT INTO TBL_CREW_POSTS(CREW_POST_CODE, CREW_POST_SUBJECT, CREW_POST_CONTENT, CREW_MEMBER_CODE)
VALUES(FN_CREATE_CODE('TBL_CREW_POSTS', SEQ_CREW_POSTS.NEXTVAL), #{crewPostSubject}, #{crewPostContent}
    , #{authorMemberCode}
)

UPDATE TBL_CREW_POSTS
SET CREW_POST_HITCOUNT=CREW_POST_HITCOUNT+1
WHERE CREW_POST_CODE=#{crewPostCode}

mapper.update:
UPDATE TBL_CREW_POSTS
SET CREW_POST_SUBJECT=#{crewPostSubject}, CREW_POST_CONTENT=#{crewPostContent}, CREW_POST_UPDATE_DATE=SYSDATE
WHERE CREW_POST_CODE=#{crewPostCode}

UPDATE TBL_CREW_POSTS
SET CREW_POST_DEL_DATE = SYSDATE
WHERE CREW_POST_CODE=#{crewPostCode}

mapper.select:
		{ call PRC_CREW_COMMENT_INSERT
			( #{authorAccountCode}
    		, #{crewCommentContent}
    		, #{crewPostCode}
		    , #{parentCommentCode, mode=IN, jdbcType=VARCHAR}
		    , #{returnValue, mode=OUT, jdbcType=VARCHAR}
			)
		}

mapper.update:
UPDATE TBL_CREW_COMMENTS
SET CREW_CMNT_CONTENT=#{crewCommentContent}, CREW_CMNT_UPDATE_DATE=SYSDATE
WHERE CREW_CMNT_CODE = #{crewCommentCode}

UPDATE TBL_CREW_COMMENTS
SET CREW_CMNT_DEL_DATE=SYSDATE
WHERE CREW_CMNT_CODE = #{crewCommentCode}

mapper.select:
SELECT VC.CREW_CODE AS crewCode , VC.CREW_NAME AS crewName , VC.CREW_OP_DATE AS crewOpenDate , VC.TITLE_ACHV_GET_CODE AS titleAchievementGetCode , VC.TITLE_ACHV_CODE AS titleAchievementCode , VC.TITLE_ACHV_NAME AS titleAchievementName , VC.CREW_COURT_CODE AS crewCourtCode , VC.CREW_FAV_BTL_MODE_CODE AS crewFavoriteBattleModeCode , VC.CREW_FAV_BTL_MODE AS crewFavoriteBattleMode , VC.CREW_BTL_REFUSE_CODE AS crewBattleRefuseCode , VC.CREW_BTL_REFUSE AS crewBattleRefuse , VC.CREW_JOIN_MIN_BIRTHYEAR AS crewJoinMinBirthyear , VC.CREW_JOIN_MAX_BIRTHYEAR AS crewJoinMaxBirthyear , VC.CREW_JOIN_MIN_FPS AS crewJoinMinFairplayScore , VC.CREW_JOIN_ABL_LIMIT_CODE AS crewJoinAbilityLimitCode , VC.CREW_JOIN_MIN_TIER_CODE AS crewJoinMinTierCode , VC.CREW_JOIN_MAX_TIER_CODE AS crewJoinMaxTierCode , VC.CREW_JOIN_MIN_TIER AS crewJoinMinTierGrade , VC.CREW_JOIN_MAX_TIER AS crewJoinMaxTierGrade , VC.CREW_JOIN_MIN_ABL_SCORE AS crewJoinMinAbilityScore , VC.CREW_JOIN_MAX_ABL_SCORE AS crewJoinMaxAbilityScore , VC.LEADER_MEMBER_CODE AS leaderMemberCode , VC.LEADER_ACCT_CODE AS leaderAccountCode , VC.LEADER_NICKNAME AS leaderNickname , VC.CREW_STATUS AS crewStatus , VC.FPS AS crewFairplayScore , VP.CREW_STATUS AS crewPenaltyStatus , VP.PENALTY_COUNT AS penaltyCount , VC.CREW_PROFILE_IMG AS crewProfileImg , VC.BATTLE_COUNT AS battleCount , VC.SCORE AS battleCount , VC.CREW_GRADE AS crewGrade FROM ( SELECT ROWNUM AS RNUM, V.* FROM VIEW_CREW V
WHERE V.CREW_NAME LIKE '%${keyword}%'
ORDER BY V.SCORE DESC, V.BATTLE_COUNT DESC ) VC LEFT JOIN VIEW_CREW_PENALTY_SUMMARY VP ON VC.CREW_CODE = CP.CREW_CODE WHERE VC.RNUM >= #{start} AND VC.RNUM <= #{end}
