	}

mapper.insert:

INSERT INTO TBL_USER_BLOCK(USER_BLOCK_CODE, BLOCKING_ACCT_CODE, BLOCKED_ACCT_CODE)
VALUES(
    FN_CREATE_CODE('TBL_USER_BLOCK', SEQ_USER_BLOCK.NEXTVAL)
    , #{blockingAccountCode}
    , #{blockedAccountCode}
)

